={{ 
  (function(content, citations) {
    let updatedContent = content;
    // Loop through each citation and replace occurrences of [1], [2], ... with the source text
    for (let i = 0; i < citations.length; i++) {
      // Build the target pattern, e.g. [1], [2], etc.
      const pattern = "[" + (i + 1) + "]";
      // Build the replacement text
      const replacement = " - source: " + citations[i];

      // Perform the replacement
      updatedContent = updatedContent.replaceAll(pattern, replacement);
    }
    return updatedContent;
  })($json.choices[0].message.content, $json.citations)
}}

